shop_mutations.announcement_bar:
mutation SetAnnouncementBar {
  metafieldsSet(metafields: [{
    namespace: "shelzys"
    key: "announcement_bar"
    ownerId: "gid://shopify/Shop/SHOP_ID"
    type: "single_line_text_field"
    value: "Spring Collection is HERE! Free shipping on orders $50+ | Use code SPRING25 for 15% off"
  }]) {
    metafields { id key value }
    userErrors { field message }
  }
}

mutations.query:
mutation SetProductSellingPoints {
  productUpdate(input: {
    id: "gid://shopify/Product/PRODUCT_ID_FOR_spring-floral-bottle"
    metafields: [{
      namespace: "shelzys"
      key: "selling_points"
      type: "list.single_line_text_field"
      value: "[\"Permanent sublimation printing\",\"Double-wall insulated\",\"24hr cold / 12hr hot\",\"BPA-free and durable\"]"
    }]
  }) {
    product { id title }
    userErrors { field message }
  }
}

